import { CHANGE_SEARCHFIELD } from "./constants"

const intialState = {
    searchField:''
}

export const searchRobots = (state=intialState, action={}) => {
    switch(action.type) {
        case CHANGE_SEARCHFIELD:  
            return Object.assign({}, state, {searchField: action.playload});
        default: return state;
        }
}
